        if selectedItems.contains(userId) {
           deleteUser(id: userId)
            output?(.updateRow(index: row))
        } else {
            if selectedItems.count == 4 {
                output?(.failure(message: "You can only select 4 participants"))
                return
                
            }
            selectedItems.append(userId)
            output?(.updateRow(index: row))
        }
    }
    
